        try {
            var search = req.query.search;
            let Sliders = await models.Slider.find({
                $or:[
                    {"title": new RegExp(search, "i")},
                ]
            }).sort({'createdAt': -1});

            Sliders = Sliders.map((user) => {
                return resource.Slider.slider_list(user);
